def adjacentRolls(row, col):
    coordinates = []
    for i in range(row-1, row+2):
        for j in range(col-1, col+2):
            
            if i >= 0 and i < len(input_list) and j >= 0 and j < len(input_list[0]) and not (i == row and j == col) and input_list[i][j] == "@":
                coordinates.append([i, j])
    return coordinates

for row in range(len(input_list)):
    for col in range(len(input_list[0])):
        if input_list[row][col] == "@" and len(adjacentRolls(row, col)) < 4:
            accessible_rolls += 1

print(accessible_rolls)

